mapper.select:
SELECT  COUNT(USER_ID)
  FROM  JR_DB.EMP
 WHERE  USER_ID = #{input_id}

SELECT  COUNT(USER_ID)
  FROM  JR_DB.EMP
 WHERE  USER_ID 		= #{input_id}
   AND	USER_PASSWORD	= #{input_password}

SELECT	COUNT(*)
  FROM	JR_DB.CONNECT_RECORD
 WHERE	CONNECT_IP = #{ipAdress}
   AND	CONNECT_DATE = #{currentDate}
