mapper.select:
SELECT   A.USER_EMAIL
  FROM
  	 (
         SELECT   USER_EMAIL,
               		USER_RIGHT
           FROM   EMP

          WHERE   USER_RIGHT <=    (
				                              SELECT    BOARD_CONTROLL_RIGHT
				                                FROM   ACCEPT_RIGHT
				                               WHERE   BOARD_TYPE = #{b_type}
				                           )

      )  A
  WHERE  USER_RIGHT != 1;
