mapper.select:
SELECT   A.USER_EMAIL
  FROM
  	 (
         SELECT   USER_EMAIL,
               	  USER_RIGHT
           FROM   JR_DB.EMP

          WHERE   USER_RIGHT <=    (
				                              SELECT    BOARD_CONTROLL_RIGHT
				                                FROM   JR_DB.ACCEPT_RIGHT
				                               WHERE   BOARD_TYPE = #{BOARD_TYPE}
				                           )

      )  A
  WHERE  USER_RIGHT != 1;

SELECT	COUNT(*)
  FROM	JR_DB.EMP
 WHERE	USER_ID = #{joinId}
